import React, { Component } from "react";
import styles from "./UserCard.module.css";
import Loader from "./Loader.js";
export default class UserCard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputData: null,
      searchEnable: false
    };
  }
  componentDidMount() {
    this.props.getUsers();
    this.props.searchUserRepo();
  }

  render() {
    const userData = this.props && this.props.userData && this.props.userData;
    const repoData = this.props && this.props.repoData && this.props.repoData;

    // const image = userData.avatar_url;

    // const name = userData.login;
    console.log("userData", userData);
    console.log("repoData", repoData);
    return (
      <React.Fragment>
        <div className={styles.base}>
          <div className={styles.leftSectionWrapper}>
            <div className={styles.leftSection}>
              <div className={styles.imageAndName}>
                {image && (
                  <div className={styles.imageHolder}>
                    <img src={image} alt="" />
                  </div>
                )}

                <div className={styles.nameOfUser}>{name}</div>
              </div>
            </div>
          </div>
          <div className={styles.rightSectionWrapper}>
            <div className={styles.rightSection}></div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}
