import * as repoAction from "../actions/repo.action";
const repo = (
  state = {
    status: null,
    error: null,
    userDetails: null,
    repoDetails: null,
    user: null,
    repoLoader: false
  },
  action
) => {
  switch (action.type) {
    case repoAction.USERS_REPO_REQUEST:
      return Object.assign({}, state, {
        status: action.status,
        repoLoader: true
      });
    case repoAction.USERS_REPO_SUCCESS:
      return Object.assign({}, state, {
        repoDetails: action.userDetails,
        status: action.status
      });
    case repoAction.USERS_REPO_FAILURE:
      return Object.assign({}, state, {
        error: action.error
      });
    case repoAction.ON_CHANGE_HANDLER:
      let val = action.event.target.value;

      let suggestions = [];
      if (val.length > 0) {
        const regex = new RegExp(`^${val}`, "i");
        suggestions = state.repoDetails.sort().filter(m => regex.test(m));
      }
      this.setState({
        suggestions: suggestions
      });
      return Object.assign({}, state, {
        value: action.event.target.value,
        repoDetails: suggestions
      });
    default:
      return state;
  }
};
export default repo;
